function signin(){dsApp.post("/Account/Login",{email:$("#email").val(),password:$("#password").val(),rememberMe:$("#rememberme").prop("checked"),__RequestVerificationToken:$('input[name="__RequestVerificationToken"]').val()},function(){window.location.href="/Home"})}$(function(){$("#sign_in").validate({highlight:function(n){$(n).parents(".form-line").addClass("error")},unhighlight:function(n){$(n).parents(".form-line").removeClass("error")},errorPlacement:function(n,t){$(t).parents(".input-group").append(n)}})});